import React from "react";
import { Header, Grid, Image } from "semantic-ui-react";

const Weather = props => {
  console.log(props);

console.log(props.data.news[7].data[0].data)
  var data = props.data.news[7].data[0].data[0];
  const city_name = data["city_name"];
  let temp = data["temp"];
  let wicon = data["ico"];
  let day = data["day"];

  const forecast = props.data.news[6].data[0].data;
  let f = [];
  let count = 0;

  console.log(forecast);
  Object.entries(forecast).map((item, key) => {
    console.log(item[1].day);
    f[count] = {
      day: item[1].day,
      temp: item[1].temp,
      wicon: item[1].ico
    };
    count++;
    return f;
  });
  console.log(f);

  const weathericon = icon => {
    if (icon !== undefined && icon !== "") {
      const temp = icon;
      console.log(temp);
      const images = require.context("../../assets/icons", true);
      const weatherimage = images("./" + temp + ".svg");
      const image = (
        <Image
          src={weatherimage}
          alt={"Current Conditions"}
          size="tiny"
          centered
        />
      );
      return image;
    }
  };

  return (
    <Grid centered columns={1} style={{ background: "WhiteSmoke" }}>
      <Grid.Row centered>
        <Grid.Column textAlign="left">
          <Header size="small" style={{ margin: "0" }}>
            {city_name}
          </Header>
          <div>{day}</div>
        </Grid.Column>
        <Grid.Column textAlign="center">
          <div>{weathericon(wicon)}</div>
          <div style={{ fontSize: "36px", textAlign: "center" }}>{temp}</div>
        </Grid.Column>
      </Grid.Row>

      <Grid.Row centered columns={3} divided>
        <Grid.Column textAlign="center">
          <div>{weathericon(f[0].wicon)}</div>
          <div>{f[0].temp}</div>
          <div>{f[0].day}</div>
        </Grid.Column>
        <Grid.Column textAlign="center">
          <div>{weathericon(f[1].wicon)}</div>
          <div>{f[1].temp}</div>
          <div>{f[1].day}</div>
        </Grid.Column>
        <Grid.Column textAlign="center">
          <div>{weathericon(f[2].wicon)}</div>
          <div>{f[2].temp}</div>
          <div>{f[2].day}</div>
        </Grid.Column>
      </Grid.Row>
    </Grid>
  );
};

export default Weather;
