import React, { Component } from "react";
import { Grid, Image, Card, Icon, Label, Button, Modal, Header } from "semantic-ui-react";
import "./Cards.css";
class Cards extends Component {
  render() {
    console.log(this.props);
    const data = this.props;
    console.log(data.buttons[1]);

    return (
      <div>
        <Card
          raised
          centered
          color={data.color}
          style={{ background: "whitesmoke" }}
        >
          <img src={data.image} alt={data.label} height={200} />
          <Card.Content>
            <Label size="big" as="div" color={data.color} ribbon>
              {data.label}
            </Label>
            <Card.Meta>{data.meta}</Card.Meta>
            <Card.Description>{data.description}</Card.Description>
          </Card.Content>

          <Card.Content extra style={{ background: "white" }}>
            {/* Map over the development buttons and display them */}
            {Object.entries(data.buttons).map((button_name, v) => {
              let name = `${button_name[1]}`;
              return (
                <Button basic size="mini" labelPosition="right" >
                  <Label as="div" image style={{ background: "white" }}>
                    <img
                      src={`${name}.jpg`}
                      alt="developed with {button_name[1]}"
                      height={10}
                    />
                    <div>{name}</div>
                  </Label>
                </Button>
              );
            })}
          </Card.Content>
          <Card.Content extra style={{background:"white"}}>
            <div>
              <Button disabled={data.githublink} size="mini" labelPosition="right">
                <Label as="a">
                  <Icon name="github" size="large" color="black" />
                  Source
                </Label>
              </Button>

              <Modal trigger={<Button>Show Modal</Button>}>
    <Modal.Header>Select a Photo</Modal.Header>
    <Modal.Content image>
      <Image wrapped size='medium' src='https://react.semantic-ui.com/images/avatar/large/rachel.png' />
      <Modal.Description>
        <Header>Default Profile Image</Header>
        <p>
          We've found the following gravatar image associated with your e-mail
          address.
        </p>
        <p>Is it okay to use this photo?</p>
      </Modal.Description>
    </Modal.Content>
  </Modal>
            </div>
          </Card.Content>
        </Card>
      </div>
    );
  }
}
export default Cards;
