import React, { Component } from "react";
import { Menu, Segment, Icon, Grid } from "semantic-ui-react";
import { Link } from "react-router-dom";
import "../../App.css";
import { slide as Menu1 } from "react-burger-menu";

class NavBar extends Component {
  state = { activeItem: "home" };

  handleItemClick = (e, { name }) => this.setState({ activeItem: name });

  render() {
    var styles = {
      bmBurgerButton: {
        position: 'fixed',
        width: '20px',
        height: '20px',
        left: '20px',
        top: '20px'
      },
      bmBurgerBars: {
        background: '#373a47'
      },
      bmBurgerBarsHover: {
        background: '#a90000'
      },
      bmCrossButton: {
        height: '24px',
        width: '24px'
      },
      bmCross: {
        background: '#bdc3c7'
      },
      bmMenuWrap: {
        position: 'fixed',
        height: '100%'
      },
      bmMenu: {
        background: '#373a47',
        padding: '0 0 0 0',
        fontSize: '1.15em'
      },
      bmMorphShape: {
        fill: '#373a47'
      },
      bmItemList: {
        color: '#b8b7ad',
        padding: '0.8em'
      },
      bmItem: {
        display: 'inline-block'
      },
      bmOverlay: {
        background: 'rgba(0, 0, 0, 0.3)'
      }
    }
    console.log(this.props);
    console.log("hi");
    const { activeItem } = this.state;
    return (
      <Grid>
      <Grid.Row columns={1} only='mobile'>
        <Grid.Column>
        <Menu1 styles={ styles } >
        <Menu.Item
            className="menu-item"
            as={Link}
            to="/"
            name="home"
            active={activeItem === "home"}
            onClick={this.handleItemClick}
          >
            <div className="navbar-items">Home</div>
          </Menu.Item> 
          <Menu.Item
            as={Link}
            to="about"
            name="about"
            path="/about"
            active={activeItem === "about"}
            onClick={this.handleItemClick}
          >
            <div className="navbar-items">About Me</div>
          </Menu.Item>
        </Menu1>
        </Grid.Column>
      </Grid.Row>
      <Grid.Row columns={1} only='tablet computer'>
        <Grid.Column>
          <ComputerMenu />
        </Grid.Column>
      </Grid.Row>
    </Grid>



    );
  }
}
export default NavBar;
