import React, { Component } from "react";
import { Menu, Segment, Icon } from "semantic-ui-react";
import { Link } from "react-router-dom";
import "../../App.css";
import { slide as Menu1 } from "react-burger-menu";

class NavBar extends Component {
  state = { activeItem: "home" };

  handleItemClick = (e, { name }) => this.setState({ activeItem: name });

  render() {
    console.log(this.props);
    console.log("hi");
    const { activeItem } = this.state;
    return (
      <Segment basic>
        <Menu pointing secondary>
        <Menu1>

        </Menu1>

          <Menu.Item
            className="menu-item"
            as={Link}
            to="/"
            name="home"
            active={activeItem === "home"}
            onClick={this.handleItemClick}
          >
            <div className="navbar-items">Home</div>
          </Menu.Item>
          <Menu.Item
            className="menu-item"
            as={Link}
            to="about"
            name="about"
            path="/about"
            active={activeItem === "about"}
            onClick={this.handleItemClick}
          >
            <div className="navbar-items">About Me</div>
          </Menu.Item>
          <Menu.Item
            className="menu-item"
            as={Link}
            to="news"
            name="news"
            active={activeItem === "news"}
            onClick={this.handleItemClick}
          >
            <div className="navbar-items">News</div>
          </Menu.Item>
          <Menu.Item
            className="menu-item"
            as={Link}
            to="projects"
            name="projects"
            active={activeItem === "projects"}
            onClick={this.handleItemClick}
          >
            <div className="navbar-items">Projects</div>
          </Menu.Item>

          <Menu.Menu position="right">
            <Menu.Item
              href="https://linkedin.com/gregorypothier"
              title="Connect with me on Linkedin"
              target="_blank"
              rel="noopener noreferrer"
            >
              <div className="navbar-items">
                <Icon name="linkedin" size="large" color="blue" />
              </div>
            </Menu.Item>
            <Menu.Item
              href="https://gitlab.com/gpothier"
              title="Fork me on Gitlab"
              target="_blank"
              rel="noopener noreferrer"
            >
              <div className="navbar-items">
                <Icon name="gitlab" size="large" color="orange" />
              </div>
            </Menu.Item>
            <Menu.Item
              href="https://github.com/gregorypothier"
              title="Fork me on Github"
              target="_blank"
              rel="noopener noreferrer"
            >
              <div className="navbar-items">
                <Icon name="github" size="large" />
              </div>
            </Menu.Item>
          </Menu.Menu>
        </Menu>
      </Segment>
    );
  }
}
export default NavBar;
