import React, { Component } from "react";
import { Tab, Tabs, TabList, TabPanel } from "react-tabs";
import { List, Header, Pagination } from "semantic-ui-react";

class Feeds extends React.Component {
  state = {
    newsIndex: 0,
    title_loaded: false,
    content_loaded: false,
  };

  favicon(icon) {
    if (icon !== undefined && icon !== "") {
      const temp = icon;
      const images = require.context("../../assets/icons", true);
      const defImage = images("./" + temp + ".ico");
      const image = (
        <img
          src={defImage}
          alt={"Current Conditions"}
          style={{ width: "22px", height: "22px", marginRight: "2px" }}
        />
      );
      return image;
    }
  }

  render() {
    var data = this.props.data.news;

    const title = Object.entries(data).map((item, key) => {
      //console.log(this.props.name);
      if (item[1].name === this.props.name) {
        this.setState({ title_loaded: true });
        //console.log(item[1]);
        return Object.entries(item[1].data).map((k, v) => {
          console.log(k[1]);
          console.log(k[1].data.length);
          let newText = k[1].name.split(/(\s+)/).map(j => (
            <div key={j}>
              <b>{j}</b>
            </div>
          ));
          return (
            <Tab>
              <div
                title={k[1].title}
                style={{
                  display: "flex",
                  fontSize: "11px",
                  alignItems: "center",
                  lineHeight: "1em"
                }}
              >
                {this.favicon(`${k[1].ico}`)}
                <div>{newText}</div>
              </div>
            </Tab>
          );
        });
      }
    });

    const text = Object.entries(this.props.data.news).map((item, key) => {
      if (item[1].name === this.props.name) {
        this.setState({ content_loaded: true });
        return (
          <div key={key}>
            <List selection divided relaxed>
              {Object.entries(item[1].data).map((k, v) => {
                //console.log(typeof k[0]);
                //console.log(typeof this.state.newsIndex);
                if (Number(k[0]) === this.state.newsIndex) {
                  //console.log(k[1].name);
                  //https://stackoverflow.com/questions/39455224/is-it-possible-to-map-only-a-portion-of-an-array-array-map
                  return Object.entries(k[1].data)
                    .slice()
                    .map((a, b) => {
                      console.log(a[1]);
                      return (
                        <List.Item key={a[1].id}>
                          <a
                            href={a[1].link}
                            target="_blank"
                            rel="noopener noreferrer"
                          >
                            <Header size="small">{a[1].title}</Header>
                            <List.Content>
                              <List.Description>

                                <div style={{display:"flex", justifyContent:"space-between"}}>
                                <div>{a[1].title.slice(0, 20)}...
                                </div>
                                <div>
                                {a[1].time_stamp}
                                </div>
                                </div>
                              </List.Description>
                            </List.Content>
                          </a>
                        </List.Item>
                      );
                    });
                }
              })}
            </List>
          </div>
        );
      }
    });

    if (!this.content_loaded) {
      return <p>Loading ...</p>;
    }
    else {
      let feeds = (
        <Tabs
          selectedIndex={this.state.newsIndex}
          onSelect={newsIndex => this.setState({ newsIndex })}
        >
          <TabList>
            <div>
              <b>
                <span style={{ padding: "6px 12px", fontSize: "16px" }}>
                  {this.props.name}
                </span>
              </b>
              {title}
            </div>
          </TabList>
          {text}
        </Tabs>
      );

    }

    return feeds;
  }
}

export default Feeds;
