import React, { Component } from "react";
import { Menu, Segment, Icon, Grid } from "semantic-ui-react";
import { Link } from "react-router-dom";
import "../../App.css";
import { slide as Menu1 } from "react-burger-menu";

class MobileMenu extends Component {
  state = { activeItem: "home" };

  handleItemClick = (e, { name }) => this.setState({ activeItem: name });

  render() {
    var styles = {
      bmBurgerButton: {
        position: "fixed",
        width: "16px",
        height: "16px",
        left: "20px",
        top: "15px"
      },
      bmBurgerBars: {
        background: "#383838"
      },
      bmBurgerBarsHover: {
        background: "#a90000"
      },
      bmCrossButton: {
        height: "24px",
        width: "24px"
      },
      bmCross: {
        background: "#bdc3c7"
      },
      bmMenuWrap: {
        position: "fixed",
        height: "100%"
      },
      bmMenu: {
        background: "#383838",
        padding: "0",
        fontSize: "1.15em"
      },
      bmMorphShape: {
        fill: "#373a47"
      },
      bmItemList: {
        color: "#b8b7ad",
        padding: "0.8em",
        paddingTop: "2em"
      },
      bmItem: {
        display: ""
      },
      bmOverlay: {
        background: "rgba(0, 0, 0, 0.3)"
      }
    };

    const { activeItem } = this.state;
    return (
      <Menu pointing secondary fixed>
        <Container></Container>
        <Menu1 styles={styles}>
          <Menu.Item
            className="menu-item"
            as={Link}
            to="/"
            name="home"
            active={activeItem === "home"}
            onClick={this.handleItemClick}
          >
            <div style={{ color: "white" }}>Home</div>
          </Menu.Item>
          <Menu.Item
            as={Link}
            to="about"
            name="about"
            path="/about"
            active={activeItem === "about"}
            onClick={this.handleItemClick}
          >
            <div style={{ color: "white" }}>About Me</div>
          </Menu.Item>
          <Menu.Item
            as={Link}
            to="news"
            name="news"
            active={activeItem === "news"}
            onClick={this.handleItemClick}
          >
            <div style={{ color: "white" }}>News</div>
          </Menu.Item>
          <Menu.Item
            as={Link}
            to="projects"
            name="projects"
            active={activeItem === "projects"}
            onClick={this.handleItemClick}
          >
            <div style={{ color: "white" }}>Projects</div>
          </Menu.Item>
        </Menu1>

        <Menu.Menu position="right">
          <Menu.Item
            href="www.linkedin.com/in/gregorypothier"
            title="Connect with me on Linkedin"
            target="_blank"
            rel="noopener noreferrer"
          >
            <div className="navbar-items">
              <Icon name="linkedin" size="large" color="blue" />
            </div>
          </Menu.Item>
          <Menu.Item
            href="https://gitlab.com/gpothier"
            title="Fork me on Gitlab"
            target="_blank"
            rel="noopener noreferrer"
          >
            <div className="navbar-items">
              <Icon name="gitlab" size="large" color="orange" />
            </div>
          </Menu.Item>
          <Menu.Item
            href="https://github.com/gregorypothier"
            title="Fork me on Github"
            target="_blank"
            rel="noopener noreferrer"
          >
            <div className="navbar-items">
              <Icon name="github" size="large" />
            </div>
          </Menu.Item>
        </Menu.Menu>
      </Menu>
    );
  }
}
export default MobileMenu;
