import React, { Component } from "react";
import { Menu, Segment, Icon, Responsive, Visibility } from "semantic-ui-react";
import { Link } from "react-router-dom";
import "../../App.css";

class NavBar extends React.Component {
  state = { activeItem: "home" };

  handleItemClick = (e, { name }) => this.setState({ activeItem: name });

  render() {
    const menuStyle = {
      border: 'none',
      borderRadius: 0,
      boxShadow: 'none',
      marginBottom: '1em',
      backgroundColor: 'transparent',
      transition: 'box-shadow 0.5s ease, padding 0.5s ease',
  }

  const fixedMenuStyle = {
      backgroundColor: '#fff',
      border: '1px solid #ddd',
      boxShadow: '0px 3px 5px rgba(0, 0, 0, 0.2)',
  }

      stickTopMenu = () => this.setState({ menuFixed: true })
    unStickTopMenu = () => this.setState({ menuFixed: false })
    handleOnUpdate = (e, { width }) => this.setState({ width })


        const { menuFixed, width } = this.state
        const menuWidths = width >= Responsive.onlyTablet.maxWidth ? null : 1
        const isHomepage = typeof window !== 'undefined' && window.location.pathname === withPrefix("/");

    return (
      <Responsive as={Menu} icon='labeled' fluid widths={4} fixed='bottom' maxWidth={Responsive.onlyTablet.maxWidth}>

          <Menu.Item
            as={Link}
            to="/"
            name="home"
            active={activeItem === "home"}
            onClick={this.handleItemClick}
          >
            <div className="navbar-items">Home</div>
          </Menu.Item>
          <Menu.Item
            as={Link}
            to="about"
            name="about"
            active={activeItem === "about"}
            onClick={this.handleItemClick}
          >
            <div className="navbar-items">About Me</div>
          </Menu.Item>
          <Menu.Item
            as={Link}
            to="news"
            name="news"
            active={activeItem === "news"}
            onClick={this.handleItemClick}
          >
            <div className="navbar-items">News</div>
          </Menu.Item>
          <Menu.Item
            as={Link}
            to="projects"
            name="projects"
            active={activeItem === "projects"}
            onClick={this.handleItemClick}
          >
            <div className="navbar-items">Projects</div>
          </Menu.Item>

          <Menu.Menu position="right">
            <Menu.Item
                href="https://linkedin.com/gregorypothier"
                title="Connect with me on Linkedin"
                target="_blank"
                rel="noopener noreferrer"
            >
                <div className="navbar-items">
                  <Icon name="linkedin" size="large" color="blue" />
                </div>
            </Menu.Item>
            <Menu.Item
                href="https://gitlab.com/gpothier"
                title="Fork me on Gitlab"
                target="_blank"
                rel="noopener noreferrer"
            >
                <div className="navbar-items">
                  <Icon name="gitlab" size="large" color="orange" />
                </div>
            </Menu.Item>
            <Menu.Item
              href="https://github.com/gregorypothier"
              title="Fork me on Github"
              target="_blank"
              rel="noopener noreferrer"
            >
              <div className="navbar-items">
                <Icon name="github" size="large" />
              </div>
            </Menu.Item>
          </Menu.Menu>
      </Responsive>
    );
  }
}
export default NavBar;
