import React, { Component } from "react";
import { Grid, Image, Card, Icon, Label, Button } from "semantic-ui-react";
import "./Cards.css";
class Cards extends Component {
  render() {
    console.log(this.props);
    const data = this.props;
    console.log(data.buttons[1]);
    return (
      <div>
        <Card
          raised
          centered
          color={data.color}
          style={{ background: "whitesmoke" }}
        >
          <img src={data.image} alt={data.label} height={200} />
          <Card.Content>
            <Label size="big" as="div" color={data.color} ribbon>
              {data.label}
            </Label>
            <Card.Meta>{data.meta}</Card.Meta>
            <Card.Description>{data.description}</Card.Description>
          </Card.Content>

          <Card.Content extra style={{ background: "whitesmoke" }}>
            {/* Map over the development buttons and display them */}
            {Object.entries(data.buttons).map((button_name, v) => {
              let name = `${button_name[1]}`;
              return (
                <Button basic size="mini" labelPosition="right" >
                  <Label as="div" image style={{ background: "white" }}>
                    <img
                      src={`${name}.jpg`}
                      alt="developed with {button_name[1]}"
                      height={10}
                    />
                    <div>{name}</div>
                  </Label>
                </Button>
              );
            })}
          </Card.Content>
          <Card.Content extra>
            <div>
              <Button size="mini" labelPosition="right">
                <Label as="a">
                  <Icon name="github" size="large" color="black" />
                  Source
                </Label>
              </Button>

              <Button floated="right" size="mini" labelPosition="right">
                <Label as="a" icon>
                  <Icon name="external alternate" size="large" color="black" />
                  <div classname="button-text">Live Demo</div>
                </Label>
              </Button>
            </div>
          </Card.Content>
        </Card>
      </div>
    );
  }
}
export default Cards;
